package il.kmi.app.screens.legal

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.rememberScrollState
import androidx.compose.foundation.verticalScroll
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.compose.ui.Alignment
import androidx.compose.ui.text.style.TextAlign

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun LegalScreen(
    onBack: () -> Unit,
    initialTab: Int = 0
) {
    // 0 = תנאי שימוש, 1 = פרטיות, 2 = הצהרת נגישות
    var selectedTab by remember { mutableStateOf(initialTab.coerceIn(0, 2)) }

    Scaffold(
        topBar = {
            il.kmi.app.ui.KmiTopBar(
                title = "תנאי שימוש ומדיניות",
                showTopHome = false,   // כמו במסכי טרום-כניסה/רישום — לא להציג בית
                lockSearch  = true     // 🔒 חיפוש לא זמין עד אחרי כניסה/רישום
            )
        }
    ) { padding ->

        Column(
            modifier = Modifier
                .padding(padding)
                .padding(16.dp)
                .fillMaxSize()
        ) {
            // טאבים למעבר בין תנאי שימוש / מדיניות פרטיות / נגישות
            TabRow(selectedTabIndex = selectedTab) {
                Tab(
                    selected = selectedTab == 0,
                    onClick = { selectedTab = 0 },
                    text = { Text("תנאי שימוש") }
                )
                Tab(
                    selected = selectedTab == 1,
                    onClick = { selectedTab = 1 },
                    text = { Text("מדיניות פרטיות") }
                )
                Tab(
                    selected = selectedTab == 2,
                    onClick = { selectedTab = 2 },
                    text = { Text("הצהרת נגישות") }
                )
            }

            Spacer(Modifier.height(16.dp))

            Column(
                modifier = Modifier
                    .verticalScroll(rememberScrollState())
                    .weight(1f),
                verticalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                when (selectedTab) {
                    0 -> {
                        // 🔹 תנאי שימוש – נוסח רחב ומחמיר יותר
                        Text("1. כללי והסכמה לתנאים", style = MaterialTheme.typography.titleMedium)
                        Text("1.1. אפליקציית ק.מ.י – קרב מגן ישראלי (להלן: \"האפליקציה\") מופעלת ומנוהלת על ידי יובל פולק (להלן: \"המפעיל\").")
                        Text("1.2. תנאי שימוש אלה (להלן: \"התנאים\") מסדירים את מכלול השימוש באפליקציה, בתכנים ובשירותים המוצעים בה. השימוש באפליקציה מכל סוג שהוא מהווה את הסכמתך המלאה, הבלתי חוזרת והבלתי מסויגת לתנאים אלה, לרבות כל שינוי שייערך בהם מעת לעת.")
                        Text("1.3. אם אינך מסכים לאיזה מתנאי השימוש, הנך נדרש להפסיק מיד את השימוש באפליקציה, למחוק אותה מן המכשיר ולא לעשות בה כל שימוש נוסף.")
                        Text("1.4. האפליקציה מיועדת למשתמשים מעל גיל 18. שימוש על ידי קטינים יתאפשר רק באישור ובפיקוח אפוטרופוס חוקי ו/או במסגרת פעילות מועדון/מדריך מוסמך, והאפוטרופוס הוא הנושא במלוא האחריות לשימוש הקטין באפליקציה.")
                        Text("1.5. המפעיל רשאי לעדכן את התנאים בכל עת, לפי שיקול דעתו הבלעדי. מועד העדכון האחרון עשוי להיות מוצג בתוך האפליקציה. המשך שימוש באפליקציה לאחר עדכון התנאים מהווה הסכמה שלך לתנאים המעודכנים.")

                        Text("2. מהות האפליקציה והשירותים", style = MaterialTheme.typography.titleMedium)
                        Text("2.1. האפליקציה נועדה לספק למשתמשים מידע, תכנים, אימונים, הסברים, חומרי עזר, תיעוד השתתפות ואפשרויות תקשורת בתחום קרב מגן ישראלי (ק.מ.י), לרבות תרגילים, הסברים טכניים, חומרי לימוד, משובים ותיעוד התקדמות.")
                        Text("2.2. כל המידע והתכנים הניתנים באפליקציה הם מידע כללי וחינוכי בלבד ואינם מהווים ייעוץ מקצועי, הדרכה מוסמכת, המלצה בריאותית, ייעוץ רפואי, ייעוץ משפטי, פסיכולוגי או כל ייעוץ אחר.")
                        Text("2.3. האפליקציה אינה מחליפה אימון עם מדריך מוסמך ו/או השתתפות בשיעורים מסודרים, ואינה מבטיחה תוצאות, שיפור כושר, קבלת דרגה/חגורה או הצלחה בבחינות כלשהן.")
                        Text("2.4. המפעיל רשאי להוסיף, להסיר, לשנות או להפסיק כל שירות, פונקציה או תוכן באפליקציה, כולם או חלקם, בכל עת וללא הודעה מוקדמת.")

                        Text("3. רישיון שימוש מוגבל", style = MaterialTheme.typography.titleMedium)
                        Text("3.1. בכפוף לעמידתך המלאה בתנאים אלה, מעניק לך המפעיל רישיון שימוש אישי, מוגבל, הדיר, לא בלעדי, בלתי ניתן להעברה ובלתי ניתן לרישוי-משנה, לשימוש באפליקציה למטרות פרטיות ולימודיות בלבד, על גבי מכשיר קצה שבבעלותך או בשליטתך.")
                        Text("3.2. אין להשתמש באפליקציה לכל מטרה מסחרית, עסקית, מוסדית או ציבורית ללא קבלת רישיון מפורש, מראש ובכתב מהמפעיל.")
                        Text("3.3. המפעיל רשאי לבטל את רישיון השימוש שלך באפליקציה בכל עת, לפי שיקול דעתו הבלעדי, לרבות עקב הפרת תנאי שימוש אלה, וזאת ללא צורך במתן הודעה מוקדמת וללא שנדרש לפצות אותך.")

                        Text("4. אחריות המשתמש והצהרותיו", style = MaterialTheme.typography.titleMedium)
                        Text("4.1. השימוש באפליקציה, בתכניה ובשירותים הנלווים הוא על אחריותך הבלעדית והמלאה. כל פעולה שתבצע בהסתמך על תכני האפליקציה – לרבות ביצוע תרגילים פיזיים – נעשית לפי שיקול דעתך בלבד ועל אחריותך המלאה.")
                        Text("4.2. ביצוע תרגילי קרב מגן ופעילות גופנית כרוך מטבעו בסיכון לפציעות ו/או נזקים פיזיים. בכניסתך לאפליקציה אתה מצהיר כי מצבך הרפואי מאפשר השתתפות בפעילות גופנית וכי נועצת, במידת הצורך, ברופא/גורם רפואי מתאים.")
                        Text("4.3. אתה מתחייב לבצע כל תרגיל אך ורק בסביבה בטוחה, מתאימה וללא סיכון מיותר לעצמך ולזולת, ובכפוף לכל הדרכה והנחיה של מדריך מוסמך, ככל שישנו.")
                        Text("4.4. חל איסור מוחלט לעשות שימוש בתכני האפליקציה לצורך אלימות, תקיפה, פגיעה בגוף או ברכוש, איום, הטרדה, או לכל מטרה בלתי חוקית אחרת.")
                        Text("4.5. אתה מתחייב שלא לבצע באפליקציה כל פעולה המהווה הפרת דין, לרבות אך לא רק: חדירה לא מורשית למערכות, ניסיון לעקוף מנגנוני אבטחה, שליחת קוד זדוני, שימוש אוטומטי (\"בוטים\"), או ניסיון לשבש את פעילות האפליקציה.")

                        Text("5. שימוש בקטינים, הורים ומדריכים", style = MaterialTheme.typography.titleMedium)
                        Text("5.1. במקרה של שימוש קטין באפליקציה, האחריות המלאה לשימוש הקטין חלה על האפוטרופוס החוקי ו/או על המועדון/המדריך המנהל את המשתמש עבורו.")
                        Text("5.2. האפוטרופוס מצהיר כי הוא מודע לתכני האפליקציה, וכי הוא אחראי לפקח על השימוש של הקטין ולוודא שהשימוש נעשה באופן בטוח, זהיר ובהתאם לכל דין.")
                        Text("5.3. המפעיל לא יישא בכל אחריות לכל נזק שייגרם לקטין או לצד שלישי עקב שימוש הקטין באפליקציה שלא בהתאם להוראות אלה.")

                        Text("6. תכנים, זכויות יוצרים וקניין רוחני", style = MaterialTheme.typography.titleMedium)
                        Text("6.1. כל זכויות הקניין הרוחני באפליקציה – לרבות אך לא רק: קוד מקור, עיצוב, טקסטים, תכני וידאו ואודיו, תמונות, לוגו, סימני מסחר, תרשימים, מסדי נתונים וכל תוכן אחר – שייכות למפעיל ו/או לצדדים שלישיים שהעניקו למפעיל זכויות שימוש בהם.")
                        Text("6.2. אין להעתיק, לשכפל, לתרגם, להפיץ, לשדר, להעמיד לרשות הציבור, לבצע, להציג, למכור, להשכיר, לערוך, לעבד, להנדס לאחור, לפרק, או לעשות כל שימוש אחר בתכני האפליקציה, כולם או חלקם, אלא אם ניתנה לכך הסכמה מפורשת, מראש ובכתב מהמפעיל.")
                        Text("6.3. סימני המסחר והלוגואים המופיעים באפליקציה הם קניינם של בעליהם החוקיים, ואין לעשות בהם שימוש ללא הרשאה.")
                        Text("6.4. משתמש שמעלה לאפליקציה כל תוכן (למשל בפורום, בהודעות, בשיתוף חומרים) מצהיר כי הוא בעל כל הזכויות בתוכן וכי אין בהעלאתו כדי להפר זכויות צד ג'. המשתמש מעניק למפעיל רישיון שימוש בלתי בלעדי, עולמי וללא תמורה בתוכן זה, לצורך הפעלת האפליקציה, שיפור השירותים והצגתם למשתמשים אחרים, בהתאם לדין.")

                        Text("7. פורומים, שיתופים ותוכן משתמשים", style = MaterialTheme.typography.titleMedium)
                        Text("7.1. האפליקציה עשויה לכלול אזורים בהם ניתן לפרסם הודעות, תגובות, שאלות, תמונות או חומרים אחרים (למשל פורום, שידורי מאמן, צ'אטים). כל תוכן כזה הינו באחריותו הבלעדית של המשתמש שהעלה אותו.")
                        Text("7.2. המפעיל אינו מתחייב לפקח מראש על תוכן שמעלים משתמשים, אך רשאי, על פי שיקול דעתו, להסיר כל תוכן פוגעני, בלתי חוקי, מפר זכויות, או כל תוכן אחר שלדעתו אינו מתאים, ללא צורך בהודעה מראש וללא כל חובה לנמק.")
                        Text("7.3. חל איסור לפרסם תכנים משמיצים, גזעניים, מאיימים, פוגעניים, פורנוגרפיים, העשויים לפגוע בפרטיות הזולת, תכנים המהווים לשון הרע, הסתה, הטרדה, או כל תוכן המפר דין.")
                        Text("7.4. אין לפרסם פרטים אישיים של צדדים שלישיים ללא הסכמתם המפורשת.")

                        Text("8. שירותים ותכנים של צדדים שלישיים", style = MaterialTheme.typography.titleMedium)
                        Text("8.1. האפליקציה עשויה לעשות שימוש בשירותים חיצוניים, כגון שירותי ענן, אחסון, אבטחה, ניתוח סטטיסטי, מפות, תשלומים או שירותי צד שלישי אחרים, המופעלים על ידי גורמים חיצוניים למפעיל.")
                        Text("8.2. ייתכן שיוצגו באפליקציה קישורים לאתרים או שירותים של צדדים שלישיים. אין לראות בקישורים אלה משום המלצה, אישור, או אחריות כלשהי של המפעיל לגבי צדדים שלישיים אלה, תכניהם או שירותיהם.")
                        Text("8.3. השימוש בשירותים ובאתרים של צדדים שלישיים כפוף לתנאי השימוש ולמדיניות הפרטיות של אותם צדדים שלישיים בלבד, והמפעיל אינו אחראי להם ולא יהיה אחראי לכל נזק שייגרם עקב שימוש כאמור.")

                        Text("9. הגבלת אחריות", style = MaterialTheme.typography.titleMedium)
                        Text("9.1. האפליקציה, לרבות כל התכנים, הפונקציות והשירותים שבה, ניתנים לשימוש במתכונתם כפי שהם (AS IS) וכפי שהם זמינים (AS AVAILABLE), ללא כל מצג או אחריות, מפורשת או משתמעת, מכל סוג שהוא.")
                        Text("9.2. המפעיל אינו מתחייב כי האפליקציה תפעל ללא תקלות, ללא הפרעות, ללא שגיאות או ללא נפילות, ואינו מתחייב כי תקלות יתוקנו, וכי האפליקציה או השרתים שעליהם היא פועלת יהיו חפים מווירוסים, באגים, או רכיבים מזיקים אחרים.")
                        Text("9.3. המפעיל אינו אחראי לכל נזק, הפסד, אובדן, פגיעה בגוף, ברכוש או במוניטין, ישירים או עקיפים, תוצאתיים, עונשיים או מיוחדים, שייגרמו לך או לצד שלישי כלשהו בעקבות או בקשר עם השימוש באפליקציה, באי היכולת להשתמש בה, בהסתמכות על תכניה, בביצוע תרגילים או פעילות גופנית לפי התכנים, או עקב כל תקלה/ליקוי/עיכוב בשירות.")
                        Text("9.4. מבלי לגרוע מן האמור לעיל, ככל שחלה על המפעיל אחריות על פי דין שאינה ניתנת להתניה, אחריותו הכוללת של המפעיל לכל נזק שייגרם לך בקשר עם האפליקציה – אם תיקבע – לא תעלה בשום מקרה על הסכום הכולל ששילמת למפעיל, אם בכלל, בעבור השימוש באפליקציה במהלך 12 החודשים שקדמו לאירוע הנזק.")

                        Text("10. שיפוי", style = MaterialTheme.typography.titleMedium)
                        Text("10.1. אתה מתחייב לשפות ולפצות את המפעיל, עובדיו, מנהליו, בעלי מניותיו וכל מי מטעמו, בגין כל נזק, הפסד, אובדן, הוצאה (לרבות הוצאות משפט ושכר טרחת עורכי דין), או חיוב שייגרמו להם עקב: (א) הפרת תנאי שימוש אלה על ידך; (ב) שימוש שלא כדין באפליקציה; (ג) הפרת זכויות צד שלישי (לרבות זכויות קניין רוחני ופרטיות); (ד) כל טענה או תביעה שתועלה נגד המפעיל על ידי צד שלישי בקשר עם פעולותיך באפליקציה.")
                        Text("10.2. חובת השיפוי תחול אף לאחר הפסקת השימוש שלך באפליקציה.")

                        Text("11. שינויים, השהיה והפסקת השירות", style = MaterialTheme.typography.titleMedium)
                        Text("11.1. המפעיל רשאי, בכל עת, להפסיק, להשעות, להגביל או לשנות את האפליקציה, את תכניה או את שירותיה, כולם או חלקם, באופן זמני או קבוע, וזאת לפי שיקול דעתו הבלעדי, וללא מתן הודעה מוקדמת.")
                        Text("11.2. המפעיל רשאי לחסום את גישתך לאפליקציה, כולה או חלקה, ולמחוק חשבון משתמש, בין היתר עקב הפרת תנאי שימוש אלה, ביצוע מעשים המנוגדים לדין או העלולים לפגוע במפעיל, בשאר המשתמשים או בצדדים שלישיים.")
                        Text("11.3. לא תהיה לך כל טענה, דרישה או תביעה כלפי המפעיל בקשר עם ביצוע שינויים/השעיה/הפסקת השירות כאמור.")

                        Text("12. הודעות ויצירת קשר", style = MaterialTheme.typography.titleMedium)
                        Text("12.1. המפעיל רשאי לשלוח אליך מעת לעת הודעות, עדכונים, התראות, הודעות שירות ושינויים בתנאים באמצעות האפליקציה, בדוא\"ל, במסרונים (SMS) או באמצעי תקשורת אחרים שסיפקת.")
                        Text("12.2. בכל שאלה, פנייה או בקשה הקשורה לאפליקציה, לתנאי השימוש או למדיניות הפרטיות, ניתן לפנות למפעיל באמצעי ההתקשרות המופיעים באפליקציה.")

                        Text("13. הדין החל וסמכות השיפוט", style = MaterialTheme.typography.titleMedium)
                        Text("13.1. על תנאי שימוש אלה ועל כל שימוש באפליקציה יחולו דיני מדינת ישראל בלבד, מבלי לתת תוקף לכללי ברירת הדין הבינלאומיים.")
